    class B
    {
    public:
        B(void) : _m(0) {}
        B(int val) : _m(val) {}
        B(const A&amp;);
        // there is a direct conversion operator here
        operator A(void) const &amp; { return A(_m); }
        operator int(void) const &amp;&amp; { return _m; }
    private:
