      const ele = $(&quot;.background&quot;).get(0);

      ele.className = ele
        .className
        .replace(
          /background-([0-9])/g, 
          (_, p1) =&gt; &#39;background-&#39; + Math.max((Number(p1) + 1) % limit, 1)
