            int i = 0;
            while (i &lt; len)
            {
                var j = i + 1;
                while (j &lt; len)
                {
                    if ((list[i] + list[j]).Equals(sum))
                    {
                        item1Index = i;
                        item2Index = j;
                        break;
                    }
                    j++;
                }
                i++;
            }
            if (item1Index.HasValue &amp;&amp; item2Index.HasValue)
                return new Tuple&lt;int, int&gt;(item1Index.Value, item2Index.Value);
    
