use querySelectorAll

&lt;!-- begin snippet: js hide: false console: true babel: false --&gt;

&lt;!-- language: lang-js --&gt;

    let hrefs = [...document.querySelectorAll(&#39;main a[href]&#39;)].map(({href}) =&gt; href);
