        while (*n &lt; NFIELDS &amp;&amp; *p) {            /* loop while less than NFIELDS */
            size_t len;
            if ((ep = strchr (p, delim)))       /* get pointer to delim */
                len = ep - p;
            else
                len = strlen (p);               /* or get length of final string */
            if (!(output[*n] = malloc (len + 1))) { /* allocated for string */
