    // Entry point function for the browser process.
    int main(int argc, char* argv[]) {
      // Load the CEF framework library at runtime instead of linking directly
      // as required by the macOS sandbox implementation.
      CefScopedLibraryLoader library_loader;
      if (!library_loader.LoadInMain())
        return 1;
    
      // Provide CEF with command-line arguments.
      CefMainArgs main_args(argc, argv);
    
      @autoreleasepool {
        // Initialize the SimpleApplication instance.
        [SimpleApplication sharedApplication];
    
        // Specify CEF global settings here.
        CefSettings settings;
    
        // When generating projects with CMake the CEF_USE_SANDBOX value will be
        // defined automatically. Pass -DUSE_SANDBOX=OFF to the CMake command-line
        // to disable use of the sandbox.
    #if !defined(CEF_USE_SANDBOX)
        settings.no_sandbox = true;
    #endif
    
        // SimpleApp implements application-level callbacks for the browser process.
        // It will create the first browser instance in OnContextInitialized() after
        // CEF has initialized.
        CefRefPtr&lt;SimpleApp&gt; app(new SimpleApp);
    
        // CEF applications have multiple sub-processes (render, plugin, GPU, etc)
        // that share the same executable. This function checks the command-line and,
        // if this is a sub-process, executes the appropriate logic.
        CefExecuteProcess(main_args, app, nullptr);
    
        // Initialize CEF for the browser process.
        CefInitialize(main_args, settings, app.get(), NULL);
    
        // Create the application delegate.
        NSObject* delegate = [[SimpleAppDelegate alloc] init];
        [delegate performSelectorOnMainThread:@selector(createApplication:)
                                   withObject:nil
                                waitUntilDone:NO];
    
        // Run the CEF message loop. This will block until CefQuitMessageLoop() is
        // called.
        CefRunMessageLoop();
    
        // Shut down CEF.
        CefShutdown();
    
        // Release the delegate.
    #if !__has_feature(objc_arc)
        [delegate release];
    #endif  // !__has_feature(objc_arc)
        delegate = nil;
      }  // @autoreleasepool
    
      return 0;
    }

