       val: int
    end
    
    rule &quot;1&quot;
    salience 100
        when
            exists(Dummy())
        then
            System.out.println(drools.getRule().getName());
    end
    
    rule &quot;2&quot;
    salience 90
        when
            notDummy(())
        then
            insert(new Dummy(1));
            System.out.println(drools.getRule().getName());
    end

    rule &quot;3&quot;
    salience 80
        when
            // always execute 
        then
            insert(new Dummy(2));
            System.out.println(drools.getRule().getName());
    end
    
    rule &quot;4&quot;
    salience 70
        when
            $d: Dummy() 
        then
            delete($d); 
            System.out.println(drools.getRule().getName());
