            &lt;/xsl:when&gt;
            &lt;xsl:otherwise&gt;
                &lt;xsl:value-of select=&quot;&#39;Directory&#39;&quot; /&gt;
            &lt;/xsl:otherwise&gt;
        &lt;/xsl:choose&gt;       
    &lt;/xsl:template&gt;  

