        }
      }, function(e) {
        $(this).css(&quot;cursor&quot;, &quot;none&quot;);
        $(&quot;.cursor&quot;).removeClass(&quot;overlink&quot;);
      });
    }

    if (window.innerWidth &lt;= 768) {
