    super(props);
    this.state = {
      expanded: []
    };
  }

  handleToggle(id) {
    this.setState(state =&gt; {
      if (state.isExpanded.includes(id)) {
        return state.isExpanded.filter(elId =&gt; elId !== id);
      }
      return [...state.expanded, id];
