        &lt;field name=&quot;base_currency_id&quot; invisible=&quot;True&quot;/&gt;
        &lt;field name=&quot;amount_in_base&quot; widget=&quot;monetary&quot; options=&quot;{&#39;currency_field&#39;: &#39;base_currency_id&#39;}&quot;/&gt;
    &lt;/xpath&gt;

