        stringstream ss(&quot;0.007447&quot;);
        ss &gt;&gt; value;
        if (value &lt;= 0.007447) {
            cout &lt;&lt; &quot;value &lt;= 0.007447 ==&gt; TRUE -- as expected\n&quot;;
        } else {
            cout &lt;&lt; &quot;value &lt;= 0.007447 ==&gt; FALSE -- unexpected!\n&quot;;
