    name = &quot;Bob&quot; 
    &#39;Hello, {}&#39;.format(name)
    &#39;Hello, Bob&#39;
    
    name = &quot;Eric&quot;
    age = 74
    f&quot;Hello, {name}. You are {age}.&quot;
