    echo &quot;text1&quot; . &quot;text2&quot;; . &quot;text3&quot;; // wrong
    echo &quot;text1&quot; . &quot;text2&quot; . &quot;text3&quot;; // correct
    echo &quot;text1&quot; . function(); . &quot;text3&quot;; // wrong
